// Pega aquí el último código de scripts.js que te proporcioné.
// Es el que empieza con document.addEventListener('DOMContentLoaded', () => { ... });
// y contiene todas las funciones (setupRegistroPage, handleRegistroSubmit, setupCredentialPage, etc.).
// No lo vuelvo a pegar aquí para no hacer la respuesta excesivamente larga, 
// pero es el código completo del penúltimo mensaje.
document.addEventListener('DOMContentLoaded', () => {

    const initialRecintos = [
        { "id": 1, "name": "PAILON-U.E. DR ANGEL FOIANINI BANZER", "category": "recinto_pailon", "image": "images/recintos/pailon.png" },
        { "id": 2, "name": "TRES CRUCES-U.E. OSWALDO IRIGOYEN", "category": "recinto_tres_cruces", "image": "images/recintos/tres_cruces.png" },
        { "id": 3, "name": "PAILON-U.E. NACIONAL 13 DE MAYO PRIMARIO", "category": "recinto_pailon", "image": "images/recintos/pailon.png" },
        { "id": 4, "name": "PAILON-U.E. PROF. CARLOS GOMEZ CORNEJO", "category": "recinto_pailon", "image": "images/recintos/pailon.png" },
        { "id": 5, "name": "PAILON-U.E. NACIONAL 13 DE MAYO SECUNDARIO", "category": "recinto_pailon", "image": "images/recintos/pailon.png" },
        { "id": 6, "name": "NUEVA BELEN-U.E. MATEO KULJIS", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 7, "name": "EL CERRO CONCEPCION-U.E. SILVERIO LEAÑOS ROCA DEL TINTO", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 8, "name": "25 DE MAYO-U.E. EDUARDO AVAROA", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 9, "name": "POZA VERDE-ESC. POZA VERDE", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 10, "name": "PUESTO PAZ-ESC. PUESTO PAZ", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 11, "name": "ROSAL CENTRO-U.E. ROSAL CENTRO MODULO", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 12, "name": "ROSAL CENTRO-U.E. ROSAL CENTRO", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 13, "name": "CAÑADA LARGA-U.E. CAÑADA LARGA", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 14, "name": "POZO DEL TIGRE-U.E. EMILIO SULZER SALINAS", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 15, "name": "PUERTO IBAÑEZ-U.E. ING. JORGE MAURICIO ROJAS", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 16, "name": "SINAI-U.E. SINAI", "category": "comunidades", "image": "images/recintos/comunidad.png" },
        { "id": 17, "name": "TUNAS NUEVO-ESCUELA TUNAS NUEVO", "category": "comunidades", "image": "images/recintos/comunidad.png" }
    ];

    const candidatoFotoBase64 = '';
    
    let allRecintos = [];
    let pdfCredentialGenerator = null;

    function initApp() {
        setupCommonElements();
        setupCurrentPage();
    }

    function setupCommonElements() {
        const navToggle = document.querySelector('.mobile-nav-toggle');
        const primaryNav = document.getElementById('primary-navigation');
        if (navToggle && primaryNav) {
            navToggle.addEventListener('click', () => {
                const isVisible = primaryNav.getAttribute('data-visible') === 'true';
                primaryNav.setAttribute('data-visible', !isVisible);
                navToggle.setAttribute('aria-expanded', !isVisible);
            });
        }
    }

    function setupCurrentPage() {
        if (document.getElementById('recintos-grid')) setupRegistroPage();
        if (document.getElementById('suggestion-form')) setupSugerenciasPage();
        if (document.getElementById('credential-form')) setupCredentialPage();
    }


    function setupRegistroPage() {
        const recintosRef = database.ref('recintos');
        recintosRef.once('value', (snapshot) => {
            const data = snapshot.val();
            if (data && data.length > 0 && data[0].name) {
                allRecintos = data;
                renderRecintos();
            } else {
                recintosRef.set(initialRecintos).then(() => {
                    allRecintos = initialRecintos;
                    renderRecintos();
                });
            }
        });
        const searchInput = document.getElementById('search-input');
        const categoryFilter = document.getElementById('category-filter');
        searchInput.addEventListener('input', renderRecintos);
        categoryFilter.addEventListener('change', renderRecintos);
        const recintosGrid = document.getElementById('recintos-grid');
        const registroModal = document.getElementById('registro-modal');
        const closeRegistroModalBtn = document.getElementById('close-registro-modal-btn');
        const registroForm = document.getElementById('registro-form');
        recintosGrid.addEventListener('click', (e) => {
            if (e.target.classList.contains('register-btn')) {
                const recintoId = Number(e.target.dataset.id);
                const selectedRecinto = allRecintos.find(r => r.id === recintoId);
                if (selectedRecinto) {
                    document.getElementById('recinto-id-input').value = selectedRecinto.id;
                    document.getElementById('recinto-name-display').textContent = `Te estás registrando en: ${selectedRecinto.name}`;
                    registroModal.classList.add('show');
                }
            }
        });
        closeRegistroModalBtn.addEventListener('click', () => registroModal.classList.remove('show'));
        registroForm.addEventListener('submit', handleRegistroSubmit);
    }
    
    function renderRecintos() {
        const grid = document.getElementById('recintos-grid');
        if (!grid) return;
        const searchInput = document.getElementById('search-input');
        const categoryFilter = document.getElementById('category-filter');
        const searchTerm = searchInput ? searchInput.value.toLowerCase() : '';
        const category = categoryFilter ? categoryFilter.value : 'all';
        let itemsToRender = allRecintos;
        if (category !== 'all') { itemsToRender = itemsToRender.filter(item => item.category === category); }
        if (searchTerm) { itemsToRender = itemsToRender.filter(item => item.name.toLowerCase().includes(searchTerm)); }
        grid.innerHTML = '';
        if (!itemsToRender || itemsToRender.length === 0) {
            grid.innerHTML = '<p style="text-align: center; grid-column: 1 / -1;">No se encontraron resultados.</p>';
            return;
        }
        itemsToRender.forEach(item => {
            const card = document.createElement('div');
            card.className = 'card';
            card.innerHTML = `
                <img src="${item.image}" alt="${item.name}" class="card-img">
                <div class="card-body"><h3 class="card-title">${item.name}</h3></div>
                <div class="card-footer" style="padding: 0 1.5rem 1.5rem;">
                    <button class="cta-button register-btn" data-id="${item.id}" style="width: 100%;">Registrarse Aquí</button>
                </div>`;
            grid.appendChild(card);
        });
    }

    function handleRegistroSubmit(event) {
        event.preventDefault();
        const registroError = document.getElementById('registro-error');
        const fullNameInput = document.getElementById('full-name-input');
        const ciNumberInput = document.getElementById('ci-number-input');
        const recintoId = Number(document.getElementById('recinto-id-input').value);
        const fullName = fullNameInput.value.trim();
        const ciNumber = ciNumberInput.value.trim();
        
        if (!fullName || !ciNumber) {
            registroError.textContent = 'Por favor, completa todos los campos.';
            registroError.style.display = 'block';
            return;
        }
        registroError.style.display = 'none';

        const registrationsRef = database.ref('registrations');
        
        registrationsRef.orderByChild('ciNumber').equalTo(ciNumber).once('value', snapshot => {
            if (snapshot.exists()) {
                const existingData = snapshot.val();
                const recordKey = Object.keys(existingData)[0];
                const recintoName = existingData[recordKey].recintoName;
                document.getElementById('registro-modal').classList.remove('show');
                showSuccessModal(
                    'Usuario Ya Registrado', 
                    `Usted ya se encuentra registrado en el recinto: ${recintoName}.`,
                    null,
                    true
                );
            } else {
                const recinto = allRecintos.find(r => r.id === recintoId);
                const newRegistrationRef = registrationsRef.push();
                newRegistrationRef.set({
                    recintoName: recinto.name,
                    fullName: fullName,
                    ciNumber: ciNumber,
                    timestamp: new Date().toISOString()
                }).then(() => {
                    const campaignPhoneNumber = '59174420831';
                    let message = `¡Hola! confirmo mi registro para la campaña de Jaime Eguez.\n\n*Nombre:* ${fullName}\n*N° Carnet:* ${ciNumber}\n*Comunidad/Recinto:* ${recinto.name}`;
                    const whatsappUrl = `https://wa.me/${campaignPhoneNumber}?text=${encodeURIComponent(message)}`;
                    window.open(whatsappUrl, '_blank');
                    document.getElementById('registro-modal').classList.remove('show');
                    showSuccessModal(
                        '¡Registro Exitoso!',
                        `¡Gracias, ${fullName}! Tu apoyo es vital.`,
                        () => generateCredentialPDF(fullName, ciNumber)
                    );
                    fullNameInput.value = '';
                    ciNumberInput.value = '';
                }).catch(error => {
                    showNotification('Error al registrar. Inténtalo de nuevo.', 'error');
                });
            }
        });
    }

    function setupCredentialPage() {
        const credentialForm = document.getElementById('credential-form');
        const credentialError = document.getElementById('credential-error');

        credentialForm.addEventListener('submit', (e) => {
            e.preventDefault();
            const ciToSearch = document.getElementById('ci-search-input').value.trim();
            if (!ciToSearch) return;

            database.ref('registrations').orderByChild('ciNumber').equalTo(ciToSearch).once('value', snapshot => {
                if (snapshot.exists()) {
                    credentialError.style.display = 'none';
                    const data = snapshot.val();
                    const recordKey = Object.keys(data)[0];
                    const { fullName, ciNumber } = data[recordKey];
                    generateCredentialPDF(fullName, ciNumber);
                } else {
                    credentialError.style.display = 'block';
                }
            });
        });
    }

    function generateCredentialPDF(fullName, ciNumber) {
        try {
            if (typeof window.jspdf === 'undefined') {
                return showNotification("Error: Librería para PDF no cargada.", "error");
            }
            const { jsPDF } = window.jspdf;
            const doc = new jsPDF({ orientation: 'portrait', unit: 'mm', format: 'a5' });
            const pageWidth = doc.internal.pageSize.getWidth();
            doc.setFillColor(249, 251, 231);
            doc.rect(0, 0, pageWidth, doc.internal.pageSize.getHeight(), 'F');
            doc.setDrawColor(46, 125, 50);
            doc.setLineWidth(1.5);
            doc.rect(5, 5, pageWidth - 10, doc.internal.pageSize.getHeight() - 10);
            doc.setFont('helvetica', 'bold');
            doc.setFontSize(16);
            doc.setTextColor(46, 125, 50);
            doc.text('CARNET DE MILITANCIA', pageWidth / 2, 20, { align: 'center' });
            doc.setFontSize(12);
            doc.text('PARTIDO DEMÓCRATA CRISTIANO (PDC)', pageWidth / 2, 28, { align: 'center' });
            doc.addImage(candidatoFotoBase64, 'PNG', (pageWidth / 2) - 30, 35, 60, 60);
            doc.setFontSize(20);
            doc.text('Prof. Jaime Eguez Roman', pageWidth / 2, 105, { align: 'center' });
            doc.setFontSize(14);
            doc.setFont('helvetica', 'normal');
            doc.text('ALCALDE DE PAILÓN 2026-2030', pageWidth / 2, 113, { align: 'center' });
            doc.setLineWidth(0.5);
            doc.line(20, 125, pageWidth - 20, 125);
            doc.setFont('helvetica', 'normal');
            doc.setFontSize(11);
            doc.setTextColor(100, 100, 100);
            doc.text('Nombre del Miembro:', 20, 140);
            doc.setFont('helvetica', 'bold');
            doc.setFontSize(18);
            doc.setTextColor(46, 125, 50);
            doc.text(fullName.toUpperCase(), 20, 150);
            doc.setFont('helvetica', 'normal');
            doc.setFontSize(11);
            doc.setTextColor(100, 100, 100);
            doc.text(`C.I: ${ciNumber}`, 20, 157);
            doc.setFontSize(9);
            doc.setFont('helvetica', 'italic');
            doc.setTextColor(100, 100, 100);
            const footerText = 'Atención: Para que sea válida esta credencial, comuníquese con el representante de su comunidad.';
            doc.text(doc.splitTextToSize(footerText, pageWidth - 40), pageWidth / 2, 185, { align: 'center' });
            doc.output('dataurlnewwindow');
        } catch (error) {
            console.error("Error al generar PDF:", error);
            showNotification("Error al generar PDF. La imagen del candidato puede estar corrupta.", "error");
        }
    }
    
    function setupSugerenciasPage() {
        const suggestionForm = document.getElementById('suggestion-form');
        if (!suggestionForm) return;

        suggestionForm.addEventListener('submit', (e) => {
            e.preventDefault();
            const formData = new FormData(suggestionForm);
            const name = formData.get('name');
            const community = formData.get('community');
            const suggestion = formData.get('suggestion');
            const campaignPhoneNumber = '59174420831';
            let message = `¡Hola! Tengo una sugerencia para la campaña.\n\n*De:* ${name}\n*Comunidad:* ${community}\n\n*Sugerencia:*\n${suggestion}`;
            const whatsappUrl = `https://wa.me/${campaignPhoneNumber}?text=${encodeURIComponent(message)}`;
            window.open(whatsappUrl, '_blank');
            showSuccessModal('¡Sugerencia Recibida!', '¡Muchas gracias por tu valioso aporte!');
            suggestionForm.reset();
        });
    }

    function showNotification(message, type = 'success') {
        const el = document.getElementById('notification');
        if (!el) return;
        el.textContent = message;
        el.style.backgroundColor = type === 'error' ? '#D32F2F' : '#388E3C';
        el.classList.add('show');
        setTimeout(() => el.classList.remove('show'), 3000);
    }

    function showSuccessModal(title, message, pdfGenFunc = null, isDuplicate = false) {
        const modal = document.getElementById('success-modal');
        if (!modal) return;
        modal.querySelector('#success-modal-title').textContent = title;
        modal.querySelector('#success-modal-message').textContent = message;
        
        const pdfBtn = modal.querySelector('#generate-pdf-btn');
        const closeBtn = modal.querySelector('#close-success-modal-btn');

        const newPdfBtn = pdfBtn.cloneNode(true);
        pdfBtn.parentNode.replaceChild(newPdfBtn, pdfBtn);

        if (isDuplicate) {
            newPdfBtn.textContent = 'Ir a Reimprimir';
            newPdfBtn.style.display = 'inline-block';
            newPdfBtn.addEventListener('click', () => {
                window.location.href = 'credencial.html';
            });
        } else if (pdfGenFunc) {
            newPdfBtn.textContent = 'Imprimir Credencial';
            newPdfBtn.style.display = 'inline-block';
            newPdfBtn.addEventListener('click', () => {
                pdfGenFunc();
                modal.classList.remove('show');
            });
        } else {
            newPdfBtn.style.display = 'none';
        }
        modal.classList.add('show');
        if (!closeBtn.dataset.listener) {
             closeBtn.addEventListener('click', () => modal.classList.remove('show'));
             closeBtn.dataset.listener = 'true';
        }
    }

    initApp();
});